    private void Filter(ChangeEventArgs changeEventArgs)
    {
        string? filterByName = "";
        try
        {
            filterByName = changeEventArgs.Value.ToString();
        }
        catch (Exception e){}
        if (filterByName != "")
        {
            peopleToShow = persons.Where(t => t.FirstName == filterByName).ToList();
        }
        else
        {
            peopleToShow = persons;
        }
    }
    
    
